INSERT INTO notice (noticeId, title, content, writer, view, createdAt, updatedAt, isPinned) VALUES
                                                                                                (1, '주차장 정기점검 안내', '스마트파킹 시스템 점검으로 7월 5일(금) 00:00 ~ 06:00 동안 주차장 이용이 제한됩니다.', '관리자', 15, '2025-06-28 15:32:01', '2025-06-28 15:32:01', 0),
                                                                                                (2, '무인정산기 결제 오류 복구 안내', '무인정산기에서 카드 결제가 되지 않았던 문제가 복구되었습니다. 이용에 불편을 드려 죄송합니다.', '관리자', 8, '2025-06-28 15:32:01', '2025-06-28 15:32:01', 0),
                                                                                                (3, '친환경차 할인 혜택 안내', '전기차, 수소차 등 친환경 차량은 주차요금 50% 감면 혜택이 적용됩니다. 자세한 내용은 할인정보 메뉴를 참고해주세요.', '운영팀', 25, '2025-06-28 15:32:01', '2025-06-28 15:32:01', 0),
                                                                                                (4, '설 연휴 주차장 운영시간 안내', '설 연휴 기간 주차장 운영시간은 정상 운영됩니다. 이용에 참고 부탁드립니다.', '운영팀', 30, '2025-06-28 15:32:01', '2025-06-28 15:32:01', 0),
                                                                                                (5, '스마트파킹 앱 업데이트 안내', '스마트파킹 앱이 새롭게 업데이트 되었습니다. 더 편리해진 기능을 이용해보세요!', '홍길동', 12, '2025-06-28 15:32:01', '2025-06-28 15:32:01', 0),
                                                                                                (6, '서버 점검 안내', '2025년 7월 1일 서버 점검이 예정되어 있습니다.', '관리자', 120, '2025-06-28 10:00:00', '2025-06-28 10:00:00', 1),
                                                                                                (7, '이용약관 변경 안내', '이용약관이 일부 변경되었습니다. 자세한 내용은 홈페이지를 참조하세요.', '관리자', 85, '2025-05-15 09:30:00', '2025-05-15 09:30:00', 1),
                                                                                                (8, '7월 이벤트 안내', '7월 한 달간 다양한 이벤트가 진행됩니다. 많은 참여 바랍니다!', '홍보팀', 250, '2025-06-10 14:20:00', '2025-06-10 14:20:00', 0),
                                                                                                (9, '새로운 기능 추가 안내', '앱에 새 기능이 추가되었습니다. 업데이트 후 사용해 주세요.', '개발팀', 150, '2025-04-05 16:45:00', '2025-04-05 16:45:00', 0),
                                                                                                (10, '고객 만족도 설문조사', '고객 만족도 향상을 위한 설문조사에 참여해 주세요.', '관리자', 90, '2024-12-20 11:15:00', '2024-12-20 11:15:00', 0),
                                                                                                (11, '서비스 개선 의견 조사', '서비스 개선을 위해 여러분의 의견을 듣고자 설문조사를 실시합니다.', '관리자', 70, '2024-11-30 13:00:00', '2024-11-30 13:00:00', 0),
                                                                                                (12, '가을 맞이 주차 할인 이벤트', '10월 한 달간 주차요금 20% 할인 이벤트가 진행됩니다. 많은 참여 부탁드립니다!', '홍보팀', 45, '2024-09-25 09:00:00', '2024-09-25 09:00:00', 0),
                                                                                                (13, '겨울철 안전 운행 캠페인', '겨울철 안전한 주차와 운행을 위한 캠페인이 12월부터 시작됩니다. 안전 운전에 동참해 주세요.', '운영팀', 35, '2024-11-15 14:30:00', '2024-11-15 14:30:00', 0),
                                                                                                (14, '2024년 연말 감사 이벤트', '2024년 한 해 동안 이용해주신 고객님께 감사드리며, 연말 감사 이벤트를 진행합니다.', '관리자', 55, '2024-12-10 10:00:00', '2024-12-10 10:00:00', 0),
                                                                                                (15, '봄맞이 앱 리뉴얼 안내', '2025년 3월, 스마트파킹 앱이 새롭게 리뉴얼됩니다. 더 편리해진 기능을 기대해 주세요!', '개발팀', 60, '2025-02-20 13:45:00', '2025-02-20 13:45:00', 0),
                                                                                                (16, '5월 가정의 달 이벤트', '5월 한 달간 가족과 함께 주차장 이용 시 특별 이벤트 쿠폰을 드립니다.', '홍보팀', 40, '2025-04-25 11:20:00', '2025-04-25 11:20:00', 0),
                                                                                                (17, '여름철 무인정산기 이용 안내', '무인정산기 점검 및 이용 방법 안내입니다. 여름철에도 원활한 이용 부탁드립니다.', '운영팀', 30, '2025-06-01 09:10:00', '2025-06-01 09:10:00', 0),
                                                                                                (18, '추석 연휴 주차장 운영 안내', '추석 연휴 기간 동안 주차장이 정상 운영됩니다. 이용에 참고 부탁드립니다.', '운영팀', 28, '2024-09-15 08:00:00', '2024-09-15 08:00:00', 0),
                                                                                                (19, '겨울철 타이어 교체 캠페인', '겨울철 안전 운행을 위해 타이어 교체 캠페인을 진행합니다. 참여해 주세요.', '홍보팀', 22, '2024-11-20 10:30:00', '2024-11-20 10:30:00', 0),
                                                                                                (20, '서버 보안 점검 공지', '서버 보안 점검으로 9월 12일 02:00~04:00까지 서비스가 일시 중단됩니다.', '관리자', 60, '2024-09-10 09:00:00', '2024-09-10 09:00:00', 0),
                                                                                                (21, '주차장 냉방기 점검 안내', '주차장 냉방기 점검이 7월 8일 진행됩니다. 불편을 드려 죄송합니다.', '관리자', 18, '2024-07-05 14:00:00', '2024-07-05 14:00:00', 0),
                                                                                                (22, '스마트파킹 앱 오류 수정 안내', '최근 보고된 앱 결제 오류가 수정되어 정상 서비스 중입니다.', '개발팀', 35, '2024-05-10 16:45:00', '2024-05-10 16:45:00', 0),
                                                                                                (23, '주차장 CCTV 설치 안내', '주차장 안전 강화를 위해 CCTV가 설치되었습니다. 안전하게 이용해 주세요.', '운영팀', 50, '2024-03-22 09:15:00', '2024-03-22 09:15:00', 0),
                                                                                                (24, '봄맞이 주차장 대청소 행사', '봄맞이 대청소 행사가 3월 30일 개최됩니다. 많은 참여 바랍니다.', '홍보팀', 40, '2024-03-01 12:00:00', '2024-03-01 12:00:00', 0),
                                                                                                (25, '신규 요금 정책 안내', '2024년 9월부터 새로운 요금 정책이 적용됩니다. 자세한 내용은 홈페이지를 참조하세요.', '관리자', 23, '2024-06-01 10:00:00', '2024-06-01 10:00:00', 0),
                                                                                                (26, '이벤트 종료 안내', '6월 진행되었던 주차 할인 이벤트가 종료되었습니다. 많은 참여 감사합니다.', '홍보팀', 55, '2024-06-30 18:00:00', '2024-06-30 18:00:00', 0),
                                                                                                (27, '주차장 내 공사 안내', '주차장 일부 구역에서 공사가 진행됩니다. 안전에 유의해 주세요.', '관리자', 20, '2024-04-15 08:00:00', '2024-04-15 08:00:00', 0),
                                                                                                (28, '앱 업데이트 예정 안내', '스마트파킹 앱이 8월 중 업데이트 예정입니다. 최신 버전 유지 부탁드립니다.', '개발팀', 30, '2024-07-01 09:30:00', '2024-07-01 09:30:00', 0),
                                                                                                (29, '주차장 출입문 점검 공지', '출입문 점검으로 인해 7월 3일 오전 출입에 제한이 있을 수 있습니다.', '운영팀', 12, '2024-06-28 14:00:00', '2024-06-28 14:00:00', 0),
                                                                                                (30, '이용 고객 대상 설문조사', '서비스 개선을 위한 설문조사에 참여해 주세요. 소정의 상품이 준비되어 있습니다.', '관리자', 40, '2024-05-20 11:00:00', '2024-05-20 11:00:00', 0);